document.addEventListener("DOMContentLoaded",(function(){let e=document.querySelectorAll(".faq-box"),n=document.querySelectorAll(".faq-icon");e.forEach(((n,t)=>{n.addEventListener("click",(()=>{e.forEach((e=>e.classList.remove("open"))),"open"!==e[t].classList?e[t].classList.add("open"):e[t].classList.remove("open")}))})),e.forEach(((e,t)=>{e.addEventListener("click",(()=>{n.forEach((e=>e.innerText="+")),"+"==n[t].innerText?n[t].innerText="-":n[t].innerText="+"}))}))}));